  const params = useParams();
  const DUMMY_QUOTES=[
    {id:'q1',author:"Babu" , text:'no man in this world are afraid of his falilure unless its yours'},
    {id:'q2',author:"Baby" , text:'all man in this world are afraid of her falilure unless its yours'}
    ]

    const quote=DUMMY_QUOTES.find((quote)=>quote.id===params.id)
    console.log(quote)

  return (
    <Fragment>
      <h1>Quote Detail Page</h1>
      <p>{params.quoteId}</p>
      <Route path={`/quotes/${params.quoteId}/comments`}>
          <Comments />
      </Route>
    </Fragment>
  );
};

export default QuoteDetail;
